import osimport globimport subprocessimport shutilpath = os.path.dirname(os.path.abspath(__file__))print("Updated headers path: ")cssHeadersPath = input()print("UE dir path: ")uePath = os.path.join(input(), "Engine", "Source", "Runtime")def findFile(searchedFileName):    matches = []    for root, dirnames, filenames in os.walk(uePath):        for filename in filenames:            if filename == searchedFileName:                matches.append(os.path.join(root, filename))    if len(matches) > 1:        print("Multiple matches for \"" + searchedFileName + "\" :\n" + '\n'.join(matches))        return ''    if len(matches) == 0:        print("No matches for \"" + searchedFileName + "\"")        return ''    return matches[0]    for file in glob.glob(os.path.join(cssHeadersPath, '*.h')):    finalFile = findFile(os.path.split(file)[1])    if finalFile != '':        shutil.copy(file, finalFile)input()